INSERT INTO `pages` (`id`, `slug`, `title`, `content`, `created_at`, `updated_at`) VALUES
(1, 'about-us', 'আমাদের কথা', '<p style=\"line-height: 1.5;\"><span style=\"color: rgb(18, 18, 18); font-family: &quot;Noto Serif Bengali&quot;; font-size: 18px;\">ছাপা পত্রিকা, ওয়েব পোর্টাল, দুটি মাসিক ম্যাগাজিন (কিশোর আলো ও বিজ্ঞানচিন্তা), নিয়মিত ম্যাগাজিন বর্ণিল এবং ঈদসংখ্যার মতো বিশেষ ম্যাগাজিন, প্রথমা প্রকাশন, বছরব্যাপী গোলটেবিল আলোচনা, ইভেন্টস (শিক্ষা, সাংস্কৃতিক ও সামাজিক কার্যক্রমসমূহ), প্রথম আলো ট্রাস্ট, প্রথম আলো বন্ধুসভা ইত্যাদি উদ্যোগ মিলে এক হাজারেরও বেশি কর্মী নিয়ে প্রথম আলো দেশের বৃহত্তম মিডিয়া প্রতিষ্ঠান। প্রথম আলো দেশের খ্যাতনামা শিল্পগোষ্ঠী ট্রান্সকম গ্রুপের অঙ্গপ্রতিষ্ঠান মিডিয়াস্টার লিমিটেডের মালিকানাভুক্ত। ঢাকা, চট্টগ্রাম ও বগুড়া—এই তিন জায়গা থেকে প্রতিদিন প্রথম আলোর ১০টি সংস্করণ মুদ্রিত হয়। বিচিত্র সামাজিক, সাংস্কৃতিক ও শিক্ষামূলক কার্যক্রমের উদ্যোক্তা হিসেবে বলা হয়ে থাকে, ‘প্রথম আলো পত্রিকার চেয়ে একটু বেশি’।</span></p>', '2025-02-18 16:43:09', '2025-02-18 17:05:24'),
